import React, { useState } from "react";
import Button from "react-bootstrap/Button";
import Container from "react-bootstrap/Container";

const TodoList = () => {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState("");

  const AddTask = () => {
    if (newTask !== "") {
      setTasks([...tasks, { text: newTask, completed: false }]);
      setNewTask("");
    }
  };

  const removeTask = (index) => {
    const updatedTasks = [...tasks];
    const newList = updatedTasks.filter((elt, i) => i !== index);
    setTasks(updatedTasks);
    console.log(tasks);
  };

  const handleTaskChange = (index) => {
    const updatedTasks = [...tasks];
    updatedTasks[index].completed = !updatedTasks[index].completed;
    const completedTask = updatedTasks.splice(index, 1)[0];
    // updatedTasks[index].completed === false
    //   ? updatedTasks.push(completedTask)
    //   : updatedTasks.unshift(completedTask);
    updatedTasks.push(completedTask);
    setTasks(updatedTasks);
    // console.log(updatedTasks, completedTask);
  };

  return (
    <div>
      <header className="header">
        <h1>To Do List</h1>
      </header>
      <div>
        <Container>
          <ul className="list">
            {tasks.map((task, index) => (
              <li
                key={index}
                style={{
                  textDecoration: task.completed ? "line-through" : "none",
                }}
              >
                <input
                  type="checkbox"
                  checked={task.completed}
                  onChange={() => handleTaskChange(index)}
                />
                {task.text}
                <Button onClick={removeTask}>Remove Task</Button>
              </li>
            ))}
          </ul>
        </Container>
        <Container className="form">
          <input
            type="text"
            value={newTask}
            onChange={(e) => setNewTask(e.target.value)}
            autoFocus
          />
          <Button onClick={AddTask}>Add Task</Button>
        </Container>
      </div>
    </div>
  );
};

export default TodoList;
