import React, { useState, useEffect } from "react";
import "bootstrap/dist/css/bootstrap.css";
import Button from "react-bootstrap/Button";
import Container from "react-bootstrap/Container";

const TodoList = () => {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState("");

  useEffect(() => {
    // Vérifier s'il y a une liste de tâches dans le localStorage
    const storedTasks = localStorage.getItem("tasks");
    if (storedTasks) {
      // Si une liste de tâches est trouvée, la récupérer et la mettre à jour dans l'état
      setTasks(JSON.parse(storedTasks));
    }
  }, []);

  const AddTask = () => {
    if (newTask !== "") {
      setTasks([{ text: newTask, completed: false }, ...tasks]);
      localStorage.setItem(
        "tasks",
        JSON.stringify([{ text: newTask, completed: false }, ...tasks])
      );
      setNewTask("");
    }
  };

  const removeTask = (index) => {
    const updatedTasks = tasks.filter((_, i) => i !== index);
    setTasks(updatedTasks);
    localStorage.setItem("tasks", JSON.stringify(updatedTasks));
  };

  const handleTaskChange = (index) => {
    const updatedTasks = [...tasks];
    const taskArray = [...tasks];
    updatedTasks[index].completed = !updatedTasks[index].completed;
    const completedTask = taskArray.splice(index, 1)[0];

    // if (updatedTasks[index].completed) {
    //   taskArray.push(completedTask);
    // } else {
    //   taskArray.unshift(completedTask);
    // }

    updatedTasks[index].completed === true
      ? taskArray.push(completedTask)
      : taskArray.unshift(completedTask);

    setTasks(taskArray);
    localStorage.setItem("tasks", JSON.stringify(taskArray));
  };

  // return (
  //   <div>
  //     <header className="header">
  //       <h1>To Do List</h1>
  //     </header>
  //     <div className="container-sm">
  //       <Container>
  //         <ul className="list">
  //           {tasks.map((task, index) => (
  //             <li
  //               key={index}
  //               style={{
  //                 textDecoration: task.completed ? "line-through" : "none",
  //               }}
  //             >
  //               <input
  //                 type="checkbox"
  //                 checked={task.completed}
  //                 onChange={() => handleTaskChange(index)}
  //               />
  //               &nbsp;&nbsp;
  //               {task.text}
  //               <Button onClick={() => removeTask(index)}>Remove Task</Button>
  //             </li>
  //           ))}
  //         </ul>
  //       </Container>
  //       <Container className="form">
  //         <input
  //           type="text"
  //           value={newTask}
  //           onChange={(e) => setNewTask(e.target.value)}
  //           autoFocus
  //         />
  //         <Button onClick={AddTask}>Add Task</Button>
  //       </Container>
  //     </div>
  //   </div>
  // );

  return (
    <div className="bg-secondary text-grey-200">
      <header className="text-center">
        <h1>To Do List</h1>
      </header>
      <div className="container">
        <div className="row">
          <div className="col-2"></div>
          <ul className="list-group col">
            {tasks.map((task, index) => (
              <li
                key={index}
                className="bg-grey-200 p-2 text-grey-700 bg-opacity-75 list-group-item d-flex justify-content-between align-items-center"
                style={{
                  textDecoration: task.completed ? "line-through" : "none",
                }}
              >
                <div className="col">
                  <input
                    type="checkbox"
                    checked={task.completed}
                    onChange={() => handleTaskChange(index)}
                  />
                  &nbsp;&nbsp;&nbsp;&nbsp;
                  {task.text}
                </div>
                <div className="col-2">
                  <Button variant="danger" onClick={() => removeTask(index)}>
                    Remove Task
                  </Button>
                </div>
              </li>
            ))}
          </ul>
          <div className="col-2"></div>
        </div>

        <div className="row">
          <div className="col-12 d-flex justify-content-center align-items-center">
            <div className="col-4">
              <input
                type="text"
                value={newTask}
                onChange={(e) => setNewTask(e.target.value)}
                autoFocus
              />
            </div>
            <div className="col-2">
              <Button onClick={AddTask} variant="light">
                Add Task
              </Button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default TodoList;
