import React, { useState, useEffect } from "react";
import Button from "react-bootstrap/Button";
import Container from "react-bootstrap/Container";

const TodoList = () => {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState("");

  useEffect(() => {
    // Vérifier s'il y a une liste de tâches dans le localStorage
    const storedTasks = localStorage.getItem("tasks");
    if (storedTasks) {
      // Si une liste de tâches est trouvée, la récupérer et la mettre à jour dans l'état
      setTasks(JSON.parse(storedTasks));
    }
  }, []);

  const AddTask = () => {
    if (newTask !== "") {
      setTasks([...tasks, { text: newTask, completed: false }]);
      localStorage.setItem(
        "tasks",
        JSON.stringify([...tasks, { text: newTask, completed: false }])
      );
      setNewTask("");
    }
  };

  const removeTask = (index) => {
    const updatedTasks = tasks.filter((_, i) => i !== index);
    setTasks(updatedTasks);
    localStorage.setItem("tasks", JSON.stringify(updatedTasks));
  };

  const handleTaskChange = (index) => {
    let updatedTasks = [...tasks];
    const taskArray = [...tasks];
    updatedTasks[index].completed = !updatedTasks[index].completed;
    const completedTask = taskArray.splice(index, 1)[0];
    // console.log(updatedTasks, completedTask, updatedTasks[index].completed);
    if (updatedTasks[index].completed) {
      taskArray.push(completedTask);
    } else {
      taskArray.unshift(completedTask);
    }
    setTasks(updatedTasks);
    localStorage.setItem("tasks", JSON.stringify(updatedTasks));
    // console.log(updatedTasks, completedTask);
  };

  return (
    <div>
      <header className="header">
        <h1>To Do List</h1>
      </header>
      <div>
        <Container>
          <ul className="list">
            {tasks.map((task, index) => (
              <li
                key={index}
                style={{
                  textDecoration: task.completed ? "line-through" : "none",
                }}
              >
                <input
                  type="checkbox"
                  checked={task.completed}
                  onChange={() => handleTaskChange(index)}
                />
                &nbsp;&nbsp;
                {task.text}
                <Button onClick={() => removeTask(index)}>Remove Task</Button>
              </li>
            ))}
          </ul>
        </Container>
        <Container className="form">
          <input
            type="text"
            value={newTask}
            onChange={(e) => setNewTask(e.target.value)}
            autoFocus
          />
          <Button onClick={AddTask}>Add Task</Button>
        </Container>
      </div>
    </div>
  );
};

export default TodoList;
